import {createSlice} from '@reduxjs/toolkit';

const initialCartState = {value: 0 , counter: 0}
const cartUpdateSlice = createSlice({
    name : 'cartUpdate',
    initialstate : initialCartState,
    reducers : {
        updateValue(state,action) {
            state.value = state.value + action.payload;
        },

       // updateTotal(state) {
           // state.total = state.total+ state.
       // },
        increment(state) {
            state.counter++;
            state.value= state.value*state.counter;
        },
        decrement(state) {
            state.counter--;
            state.value= state.value*state.counter;
        }
    }
});

export const cartUpdateActions = cartUpdateSlice.actions;

export default cartUpdateSlice.reducer;
