import classes from './CartItem.module.css';
import {useSelector, useDispatch} from 'react-redux/es/exports';
import { cartUpdateActions } from '../../store/cartUpdateSlice';


const CartItem = (props) => {
  const { title, quantity, total, price } = props.item;

  const newTotal = useSelector(state => state.cartUpdate.value);

  const dispatch = useDispatch();
  const incHandler = () => {
    dispatch(cartUpdateActions.increment);
  };

  const decHandler = () => {
    dispatch(cartUpdateActions.decrement);
  };

  const newQuantity = useSelector(state => state.cartUpdate.counter);



  return (
    <li className={classes.item}>
      <header>
        <h3>{title}</h3>
        <div className={classes.price}>
          ${newTotal.toFixed(2)}{' '}
          <span className={classes.itemprice}>(${price.toFixed(2)}/item)</span>
        </div>
      </header>
      <div className={classes.details}>
        <div className={classes.quantity}>
          x <span>{newQuantity}</span>
        </div>
        <div className={classes.actions}>
          <button onClick={decHandler}>-</button>
          <button onClick={incHandler}>+</button>
        </div>
      </div>
    </li>
  );
};

export default CartItem;
